using System;using System.Runtime.InteropServices;namespace ArmstrongNumbers{    public static class ArmstrongNumbers    {        private static string NumberAsString { get; set; }        public static bool IsArmstrongNumber(int number)        {            int sum = 0;            bool result;            NumberAsString = number.ToString();            foreach (var c in NumberAsString)            {                sum += (int) Math.Pow(char.GetNumericValue(c), NumberAsString.Length);            }            result = sum == number;                    return result;        }    }}